mapper.select:
select * from ( select rownum as rno, teacher_seq, cate_no, cate_code, cate_name, teacher_gender, teacher_name, teacher_registerday, teacher_status from ( select T.teacher_seq , C.cate_no , C.cate_code , C.cate_name , case T.teacher_gender when '1' then '남자' else '여자' end as teacher_gender , T.teacher_name , to_char(T.teacher_registerday, 'yyyy-mm-dd') as teacher_registerday , case T.teacher_status when '0' then '퇴사' when '1' then '재직' when '2' then '휴직' end as teacher_status from lec_category C JOIN tbl_teacher T on C.cate_no = T.fk_cate_no where 1=1
and C.cate_code = '성인'
and C.cate_code = '아동'
and C.cate_no = #{searchName}
and teacher_name like '%'||#{searchText}||'%'
and teacher_status = #{searchStatus}
order by teacher_seq desc ) V ) T
where rno between #{startRno} and #{endRno}

select count(*) from lec_category C JOIN tbl_teacher T on C.cate_no = T.fk_cate_no where 1=1
and C.cate_code = '성인'
and C.cate_code = '아동'
and C.cate_no = #{searchName}
and teacher_name like '%'||#{searchText}||'%'
and teacher_status = #{searchStatus}

mapper.insert:
		insert into tbl_teacher (teacher_seq, fk_cate_no, teacher_name, teacher_gender, teacher_jubun, teacher_postcode, teacher_addr1, teacher_addr2, teacher_phone1
                        		,teacher_phone2, teacher_email, teacher_shcool, teacher_major, teacher_career1
                        		,teacher_career2, teafileName, teaorgFilename, teafileSize)
		values(seq_tbl_teacher.nextval, #{fk_cate_no}, #{teacher_name}, #{teacher_gender}, #{teacher_jubun}, #{teacher_postcode}, #{teacher_addr1}, #{teacher_addr2}, #{teacher_phone1}  
			  , #{teacher_phone2}, #{teacher_email}, #{teacher_shcool}, #{teacher_major},  #{teacher_career1}
			  , #{teacher_career2}, #{teafileName}, #{teaorgFilename}, #{teafileSize})

		insert into tbl_teacher (teacher_seq, fk_cate_no, teacher_name, teacher_gender, teacher_jubun, teacher_postcode, teacher_addr1, teacher_addr2, teacher_phone1
                        		,teacher_phone2, teacher_email, teacher_shcool, teacher_major, teacher_career1
                        		,teacher_career2)
		values(seq_tbl_teacher.nextval, #{fk_cate_no}, #{teacher_name}, #{teacher_gender}, #{teacher_jubun}, #{teacher_postcode}, #{teacher_addr1}, #{teacher_addr2}, #{teacher_phone1}  
			  , #{teacher_phone2}, #{teacher_email}, #{teacher_shcool}, #{teacher_major},  #{teacher_career1}
			  , #{teacher_career2})

mapper.select:

select teafileName, teaorgFilename, teafileSize
from tbl_teacher
where teacher_seq = #{teacher_seq}
